import "./App.css";
import React, { useState, useEffect } from "react";
import About from "./components/About/About";
import Experience from "./components/Exprience/Exprience";
import Projects from "./components/Projects/Projects";
import Contact from "./components/Contact/Contact";
function App() {
  const [activeTab, setActiveTab] = useState("about");
  const [currentTime, setCurrentTime] = useState(
    new Date().toLocaleTimeString()
  );
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentTime(new Date().toLocaleTimeString());
    }, 1000); // updates every second

    return () => clearInterval(timer); // cleanup function to clear interval
  }, []);
  return (
    <div className="container">
      <h1>ANANYA SHUKLA</h1>
      <h3>FULLSTACK DEVELOPER</h3>
      <div className="socials">
        <img src="/images/github.svg" alt="github" />
        <img src="/images/linkedn.svg" alt="linkedn" />
        <img src="/images/mail.svg" alt="mail" />
      </div>
      <div className="time-and-flag">
        <p>Current Time : {currentTime} in ðŸ‡®ðŸ‡³ </p>
      </div>
      <div className="green-strip"></div>
      <div className="tabs">
        <div
          className={activeTab === "about" ? "tab active" : "tab"}
          onClick={() => setActiveTab("about")}
        >
          About Me
        </div>
        <div
          className={activeTab === "experience" ? "tab active" : "tab"}
          onClick={() => setActiveTab("experience")}
        >
          Experience
        </div>
        <div
          className={activeTab === "projects" ? "tab active" : "tab"}
          onClick={() => setActiveTab("projects")}
        >
          Projects
        </div>
        <div
          className={activeTab === "contact" ? "tab active" : "tab"}
          onClick={() => setActiveTab("contact")}
        >
          Contact
        </div>
      </div>
      <div className="content">
        {activeTab === "about" && <About />}
        {activeTab === "experience" && <Experience />}
        {activeTab === "projects" && <Projects />}
        {activeTab === "contact" && <Contact />}
      </div>
    </div>
  );
}

export default App;
